import React, { useState, useEffect } from "react";
import ListUsers from "../../components/Forum/ListUser";
// import AddStock from "../../components/Stock/AddStock";
import SearchForum from "../../components/Forum/SearchForum";
import "./forum.scss";

const Forum = ({ isMobile, username }) => {
  document.title = "FridgeMan - Forum";

  const [addVisible, setAddVisibility] = useState(false);
  const [searchVisible, setSearchVisibility] = useState(false);
  // const [posts, setPosts] = useState([]);
  const [users, setUsers] = useState([]);
  const [name, setName] = useState("");

  const getUsers = async () => {
    await fetch("/api/users")
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setUsers(data);
      });
  };

  useEffect(() => {
    getUsers();
  }, []);

  return (
    <div className="forums page">
      <div className="forum-content">
        <h3>{username}'s</h3>
        <h1>Post list</h1>

        <div className="navigation-buttons">
          <button
            onClick={() => {
              setSearchVisibility(true);
            }}
          >
            Search Post
          </button>

          <button
            onClick={() => {
              setAddVisibility(true);
            }}
            style={{ marginLeft: "auto" }}
          >
            Create Post
          </button>
        </div>

        {/* <AddStock
          locationList={locationList}
          getStocks={getStocks}
          setAddVisibility={setAddVisibility}
          addVisible={addVisible}
        /> */}

        <SearchForum
          // name={name}
          // location={location}
          // setName={setName}
          // setLocation={setLocation}
          // locationList={locationList}
          // setStocks={setStocks}
          setSearchVisibility={setSearchVisibility}
          searchVisible={searchVisible}
        />
        <ListUsers
          // locationList={locationList}
          // getUsers={getUsers}
          users={users}
          // setUsers={setUsers}
          isMobile={isMobile}
        />
        {/* <ListPosts
          locationList={locationList}
          getPosts={getPosts}
          posts={posts}
          setPosts={setPosts}
          isMobile={isMobile}
        /> */}
      </div>
    </div>
  );
};

export default Forum;
